import React from 'react';
import { motion } from 'framer-motion';
import '../styles/AnimatedBackground.css';

const AnimatedBackground = ({ colors }) => {
  // Use default colors if none provided or if colors array is too small
  const backgroundColors = colors && colors.length >= 3 
    ? colors 
    : ['#355c7d', '#6c5b7b', '#c06c84']; // Fallback to a nice gradient palette

  return (
    <div className="animated-background">
      {/* Large animated gradient circles */}
      {[...Array(3)].map((_, i) => (
        <motion.div
          key={`circle-${i}`}
          className="gradient-circle"
          style={{
            background: `radial-gradient(circle at center, ${backgroundColors[i % backgroundColors.length]} 0%, transparent 70%)`,
            top: `${25 + (i * 15)}%`, // Spread them more evenly
            left: `${20 + (i * 20)}%`, // Spread them more evenly
            opacity: 0.7, // Reduced opacity to blend better
            position: 'absolute'
          }}
          initial={{ 
            x: 0, 
            y: 0, 
            scale: 1,
          }}
          animate={{
            x: [0, 80, -80, 0],        // Less extreme movement
            y: [0, -60, 60, 0],        // Less extreme movement
            scale: [1, 1.2, 0.9, 1],   // More subtle scaling
          }}
          transition={{
            duration: 20,              // Longer duration for smoother animation
            ease: "easeInOut",         // Smoother easing
            repeat: Infinity,
            repeatType: "mirror",      // Mirror instead of loop for smoother transitions
            times: [0, 0.33, 0.66, 1]  // Control timing points
          }}
        />
      ))}

      {/* Additional smaller blobs with different colors from the palette */}
      {[...Array(10)].map((_, i) => (
        <motion.div
          key={`small-circle-${i}`}
          className="gradient-circle small-circle"
          style={{
            background: `radial-gradient(circle at center, ${backgroundColors[(i + 3) % backgroundColors.length]} 0%, transparent 70%)`,
            top: `${40 + (i * 30)}%`,
            left: `${50 + (i * 10)}%`,
            width: '70vw',
            height: '70vh',
            opacity: 0.6,
            position: 'absolute'
          }}
          initial={{ 
            x: 0, 
            y: 0, 
            scale: 1
          }}
          animate={{
            x: [0, -70, 70, 0],       // Different movement pattern
            y: [0, 50, -50, 0],       // Different movement pattern
            scale: [0.9, 1.1, 0.8, 0.9], // Different scaling pattern
          }}
          transition={{
            duration: 25,              // Even longer for more variation
            ease: "easeInOut",
            repeat: Infinity,
            repeatType: "mirror",
            times: [0, 0.33, 0.66, 1],
            delay: i * 2               // Offset animations
          }}
        />
      ))}

      {/* Overlay for better text contrast */}
      <div className="background-overlay" />
    </div>
  );
};

export default AnimatedBackground;
