import React from 'react';
import { motion } from 'framer-motion';
import '../styles/AnimatedBackground.css';

const AnimatedBackground = ({ colors }) => {
  // Use default colors if none provided
  const backgroundColors = colors && colors.length >= 3 
    ? colors 
    : ['#ff0000', '#00ff00', '#0000ff']; // Bright test colors

  return (
    <div className="animated-background">
      {/* Large animated gradient circles */}
      {[...Array(3)].map((_, i) => (
        <motion.div
          key={`circle-${i}`}
          className="gradient-circle"
          style={{
            background: `radial-gradient(circle at center, ${backgroundColors[i % backgroundColors.length]} 0%, transparent 70%)`,
            position: 'absolute',
            top: `${30 * i}%`,
            left: `${20 * i}%`
          }}
          initial={{ 
            x: 0, 
            y: 0, 
            scale: 1,
            opacity: 0.8
          }}
          animate={{
            x: [0, 200, -200, 0],         // Dramatic horizontal movement
            y: [0, -150, 150, 0],         // Dramatic vertical movement
            scale: [1, 1.3, 0.7, 1],      // Scale up and down
            opacity: [0.8, 0.6, 0.8, 0.6] // Opacity variation
          }}
          transition={{
            duration: 8,                  // Animation duration in seconds
            ease: "linear",               // Linear movement
            repeat: Infinity,             // Repeat forever
            repeatType: "loop",
            times: [0, 0.33, 0.66, 1]     // Keyframe timing points
          }}
        />
      ))}

      {/* Overlay for better text contrast */}
      <div className="background-overlay" />
    </div>
  );
};

export default AnimatedBackground;
