import React, { useState, useEffect, useRef, useCallback } from 'react';
import { getPalette } from 'color-thief-react';
import AnimatedBackground from './AnimatedBackground';
import tracks from '../data/tracks.js';
import '../styles/MusicPlayer.css';
import '../styles/AnimatedBackground.css';

const MusicPlayer = () => {
  const [trackIndex, setTrackIndex] = useState(0);
  const [currentTrack, setCurrentTrack] = useState(tracks[0]);
  const [isPlaying, setIsPlaying] = useState(false);
  const [colors, setColors] = useState(null);
  const [progress, setProgress] = useState(0);
  const [duration, setDuration] = useState(0);
  const [currentTime, setCurrentTime] = useState(0);

  const audioRef = useRef(new Audio(currentTrack.audioSrc));
  const intervalRef = useRef(null);

  // Memoize the nextTrack function
  const nextTrack = useCallback(() => {
    const newIndex = (trackIndex + 1) % tracks.length;
    setTrackIndex(newIndex);
    setCurrentTrack(tracks[newIndex]);
  }, [trackIndex]);

  // Memoize previous track function as well
  const prevTrack = useCallback(() => {
    const newIndex = (trackIndex - 1 + tracks.length) % tracks.length;
    setTrackIndex(newIndex);
    setCurrentTrack(tracks[newIndex]);
  }, [trackIndex]);

  // Memoize the startTimer function
  const startTimer = useCallback(() => {
    clearInterval(intervalRef.current);

    intervalRef.current = setInterval(() => {
      if (audioRef.current.ended) {
        nextTrack();
      } else {
        setCurrentTime(audioRef.current.currentTime);
        setProgress((audioRef.current.currentTime / audioRef.current.duration) * 100);
      }
    }, 1000);
  }, [nextTrack]);

  // Get color palette from album art
  useEffect(() => {
    const fetchColors = async () => {
      try {
        // Extract more colors than we need to have options after filtering
        const palette = await getPalette(currentTrack.albumArt, 8, 'hex', {
          crossOrigin: 'anonymous',
          quality: 10,
        });

        // Filter out white and very light colors
        const filteredPalette = palette.filter(color => {
          // Convert hex to RGB to check brightness
          const hex = color.replace('#', '');
          const r = parseInt(hex.substr(0, 2), 16);
          const g = parseInt(hex.substr(2, 2), 16);
          const b = parseInt(hex.substr(4, 2), 16);

          // Calculate perceived brightness
          // Formula: (0.299*R + 0.587*G + 0.114*B)
          const brightness = (0.299 * r + 0.587 * g + 0.114 * b);

          // Filter out very light colors (brightness > 180 out of 255)
          return brightness < 180;
        });

        // If we have enough colors after filtering, use them
        // Otherwise fall back to a preset color palette
        if (filteredPalette.length >= 5) {
          setColors(filteredPalette);
          console.log('Using filtered colors:', filteredPalette);
        } else {
          // Not enough colors after filtering, use a preset vibrant palette
          const fallbackPalette = [
            '#355c7d', '#6c5b7b', '#c06c84', '#f67280', '#f8b195'
          ];
          setColors(fallbackPalette);
          console.log('Using fallback palette, filtered colors were insufficient:', filteredPalette);
        }
      } catch (error) {
        console.error('Failed to extract colors:', error);
        // Set fallback colors on error
        setColors(['#355c7d', '#6c5b7b', '#c06c84', '#f67280', '#f8b195']);
      }
    };

    fetchColors();
  }, [currentTrack.albumArt]);

  // Handle track loading and setup
  useEffect(() => {
    audioRef.current.pause();
    audioRef.current = new Audio(currentTrack.audioSrc);

    audioRef.current.addEventListener('loadedmetadata', () => {
      setDuration(audioRef.current.duration);
    });

    audioRef.current.addEventListener('ended', nextTrack);

    if (isPlaying) {
      audioRef.current.play().catch(e => console.error("Error playing audio:", e));
      startTimer();
    }

    return () => {
      audioRef.current.removeEventListener('loadedmetadata', () => {});
      audioRef.current.removeEventListener('ended', nextTrack);
      clearInterval(intervalRef.current);
    };
  }, [currentTrack, isPlaying, nextTrack, startTimer]);

  // Handle play/pause
  useEffect(() => {
    if (isPlaying) {
      audioRef.current.play().catch(e => console.error("Error playing audio:", e));
      startTimer();
    } else {
      audioRef.current.pause();
      clearInterval(intervalRef.current);
    }
  }, [isPlaying, startTimer]);

  // Cleanup when component unmounts
  useEffect(() => {
    return () => {
      audioRef.current.pause();
      clearInterval(intervalRef.current);
    };
  }, []);

  // Toggle play/pause
  const togglePlay = () => {
    setIsPlaying(!isPlaying);
  };

  // Change track position on progress bar click
  const onProgressChange = (e) => {
    const progressBar = e.currentTarget;
    const clickPosition = (e.clientX - progressBar.getBoundingClientRect().left) / progressBar.offsetWidth;
    const newTime = clickPosition * audioRef.current.duration;

    audioRef.current.currentTime = newTime;
    setCurrentTime(newTime);
    setProgress(clickPosition * 100);
  };

  // Format time
  const formatTime = (time) => {
    if (time && !isNaN(time)) {
      const minutes = Math.floor(time / 60);
      const seconds = Math.floor(time % 60);
      return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
    }
    return "0:00";
  };

  return (
    <div className="player-container">
      {/* Animated background component */}
      <AnimatedBackground colors={colors} />

      <div className="music-player">
        <div className="album-cover">
          <img 
            src={currentTrack.albumArt} 
            alt={`${currentTrack.album} cover`}
            crossOrigin="anonymous" 
          />
        </div>

        <div className="player-controls">
          <div className="track-info">
            <h2 className="track-title">{currentTrack.title}</h2>
            <p className="track-artist">{currentTrack.artist}</p>
          </div>

          <div className="progress-container">
            <div className="progress-bar" onClick={onProgressChange}>
              <div className="progress" style={{ width: `${progress}%` }}></div>
            </div>
            <div className="time-info">
              <span className="current-time">{formatTime(currentTime)}</span>
              <span className="total-time">{formatTime(duration) || currentTrack.duration}</span>
            </div>
          </div>

          <div className="control-buttons">
            <button className="control-button" onClick={prevTrack}>
              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12.066 11.2a1 1 0 000 1.6l5.334 4A1 1 0 0019 16V8a1 1 0 00-1.6-.8l-5.333 4zM4.066 11.2a1 1 0 000 1.6l5.334 4A1 1 0 0011 16V8a1 1 0 00-1.6-.8l-5.334 4z" />
              </svg>
            </button>

            <button className="control-button play-button" onClick={togglePlay}>
              {isPlaying ? (
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 9v6m4-6v6m7-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              ) : (
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z" />
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              )}
            </button>

            <button className="control-button" onClick={nextTrack}>
              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11.933 12.8a1 1 0 000-1.6L6.6 7.2A1 1 0 005 8v8a1 1 0 001.6.8l5.333-4zM19.933 12.8a1 1 0 000-1.6l-5.333-4A1 1 0 0013 8v8a1 1 0 001.6.8l5.333-4z" />
              </svg>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MusicPlayer;
