import React from 'react';
import { motion } from 'framer-motion';
import '../styles/AnimatedBackground.css';

const AnimatedBackground = ({ colors }) => {
  // Use default colors if none provided
  const backgroundColors = colors && colors.length >= 3 
    ? colors 
    : ['#ff0000', '#00ff00', '#0000ff']; // Bright test colors

  return (
    <div className="animated-background">
      {/* Large animated gradient circles */}
      {[...Array(3)].map((_, i) => (
        <motion.div
          key={`circle-${i}`}
          className="gradient-circle"
          style={{
            background: `radial-gradient(circle at center, ${backgroundColors[i % backgroundColors.length]} 0%, rgba(255,255,255,0) 70%)`,
            top: `${30 * i}%`,
            left: `${20 * i}%`,
            opacity: 0.8, // Make it more visible
          }}
          animate={{
            x: [0, 200, -200, 0],         // More dramatic movement
            y: [0, -150, 150, 0],         // More dramatic movement
            scale: [1, 1.3, 0.7, 1],      // More dramatic scaling
            opacity: [0.8, 0.6, 0.8, 0.6] // Opacity animation for more visibility
          }}
          transition={{
            duration: 8,                  // Faster animation
            ease: "linear",               // Linear movement is more noticeable
            repeat: Infinity,
            repeatType: "loop",
            times: [0, 0.33, 0.66, 1],    // Control timing points
            delay: i * 0.5               // Less delay between animations
          }}
        />
      ))}

      {/* Overlay for better text contrast */}
      <div className="background-overlay" />
    </div>
  );
};

export default AnimatedBackground;
