import React from 'react';
import { motion } from 'framer-motion';
import '../styles/AnimatedBackground.css';

const AnimatedBackground = ({ colors }) => {
  // Use default colors if none provided or if colors array is too small
  const backgroundColors = colors && colors.length >= 5 
    ? colors 
    : ['#355c7d', '#6c5b7b', '#c06c84', '#f67280', '#f8b195']; // Fallback to a nice gradient palette with 5 colors

  return (
    <div className="animated-background">
      {/* Large primary blobs - bigger and more dynamic */}
      {[...Array(4)].map((_, i) => (
        <motion.div
          key={`circle-${i}`}
          className="gradient-circle"
          style={{
            background: `radial-gradient(circle at center, ${backgroundColors[i % backgroundColors.length]} 0%, transparent 70%)`,
            top: `${15 + (i * 20)}%`, // More spread out
            left: `${10 + (i * 25)}%`, // More spread out
            width: '120vw', // Bigger blobs
            height: '120vh', // Bigger blobs
            opacity: 0.65, // Slightly reduced opacity for better blending
            position: 'absolute'
          }}
          initial={{ 
            x: 0, 
            y: 0, 
            scale: 1,
          }}
          animate={{
            x: [0, 100, -100, 30, -30, 0], // More complex movement
            y: [0, -70, 70, -20, 20, 0],   // More complex movement
            scale: [1, 1.15, 0.95, 1.1, 0.9, 1], // More complex scaling
          }}
          transition={{
            duration: 18,              // Slightly faster
            ease: "easeInOut",         
            repeat: Infinity,
            repeatType: "mirror",      
            times: [0, 0.2, 0.4, 0.6, 0.8, 1] // More control points for smoother animation
          }}
        />
      ))}

      {/* Medium sized blobs with different movement patterns */}
      {[...Array(3)].map((_, i) => (
        <motion.div
          key={`medium-circle-${i}`}
          className="gradient-circle medium-circle"
          style={{
            background: `radial-gradient(circle at center, ${backgroundColors[(i + 2) % backgroundColors.length]} 0%, transparent 70%)`,
            top: `${30 + (i * 25)}%`,
            left: `${40 + (i * 15)}%`,
            width: '90vw',
            height: '90vh',
            opacity: 0.7,
            position: 'absolute'
          }}
          initial={{ 
            x: 0, 
            y: 0, 
            scale: 1
          }}
          animate={{
            x: [0, -90, 90, -30, 0],       // Different movement pattern
            y: [0, 60, -60, 20, 0],        // Different movement pattern
            scale: [0.9, 1.15, 0.85, 1.05, 0.9], // Different scaling pattern
          }}
          transition={{
            duration: 20,              
            ease: "easeInOut",
            repeat: Infinity,
            repeatType: "mirror",
            times: [0, 0.25, 0.5, 0.75, 1],
            delay: i * 1.5             // Less delay for more activity
          }}
        />
      ))}

      {/* Small faster moving accent blobs */}
      {[...Array(3)].map((_, i) => (
        <motion.div
          key={`small-circle-${i}`}
          className="gradient-circle small-circle"
          style={{
            background: `radial-gradient(circle at center, ${backgroundColors[(i + 4) % backgroundColors.length]} 0%, transparent 70%)`,
            top: `${50 + (i * 15)}%`,
            left: `${30 + (i * 20)}%`,
            width: '60vw', // Smaller size for contrast
            height: '60vh',
            opacity: 0.75,
            position: 'absolute'
          }}
          initial={{ 
            x: 0, 
            y: 0, 
            scale: 1
          }}
          animate={{
            x: [0, 60, -60, 20, -20, 0], // Faster, shorter movements
            y: [0, -40, 40, -10, 10, 0], // Faster, shorter movements
            scale: [1, 1.1, 0.9, 1.05, 0.95, 1], // Quick subtle scaling
          }}
          transition={{
            duration: 15,              // Fastest animation
            ease: "easeInOut",
            repeat: Infinity,
            repeatType: "mirror",
            times: [0, 0.2, 0.4, 0.6, 0.8, 1],
            delay: i * 1               // Less delay for more activity
          }}
        />
      ))}

      {/* Overlay for better text contrast */}
      <div className="background-overlay" />
    </div>
  );
};

export default AnimatedBackground;
